fun ${screenName}() = AvaUI {
    theme = Themes.${themeName}

    val email = remember { mutableStateOf("") }
    val password = remember { mutableStateOf("") }

    Column {
        fillMaxSize()
        verticalArrangement = Arrangement.Center
        horizontalAlignment = Alignment.CenterHorizontally
        padding = 24f

        // Logo
        Image {
            source = "assets://app_logo.png"
            width = 120f
            height = 120f
        }

        Spacer { height = 32f }

        // Title
        Text("${appName}") {
            font = Font.DisplayMedium
            color = theme.primary
        }

        Text("Sign in to continue") {
            font = Font.BodyLarge
            color = theme.onSurfaceVariant
        }

        Spacer { height = 48f }

        // Email field
        TextField {
            id = "email"
            placeholder = "Email address"
            leadingIcon = "mail"
            keyboardType = KeyboardType.Email
            value = email.value
            onValueChange = { email.value = it }
            fillMaxWidth()
        }

        Spacer { height = 16f }

        // Password field
        TextField {
            id = "password"
            placeholder = "Password"
            leadingIcon = "lock"
            keyboardType = KeyboardType.Password
            isPassword = true
            value = password.value
            onValueChange = { password.value = it }
            fillMaxWidth()
        }

        Spacer { height = 8f }

        // Forgot password
        Row {
            fillMaxWidth()
            horizontalArrangement = Arrangement.End

            Button("Forgot Password?") {
                variant = ButtonVariant.Text
                onClick = { navigateToForgotPassword() }
            }
        }

        Spacer { height = 24f }

        // Sign in button
        Button("${buttonLabel}") {
            variant = ButtonVariant.Filled
            fillMaxWidth()
            height = 56f
            onClick = { ${loginAction} }
        }

        Spacer { height = 16f }

        // Divider with text
        Row {
            fillMaxWidth()
            verticalAlignment = Alignment.CenterVertically

            Divider { weight = 1f }
            Text("  OR  ") {
                font = Font.BodySmall
                color = theme.onSurfaceVariant
            }
            Divider { weight = 1f }
        }

        Spacer { height = 16f }

        // Social login
        Row {
            fillMaxWidth()
            horizontalArrangement = Arrangement.SpaceEvenly

            Button("") {
                icon = "google"
                variant = ButtonVariant.Outlined
                width = 64f
                height = 56f
                onClick = { loginWithGoogle() }
            }

            Button("") {
                icon = "apple"
                variant = ButtonVariant.Outlined
                width = 64f
                height = 56f
                onClick = { loginWithApple() }
            }

            Button("") {
                icon = "facebook"
                variant = ButtonVariant.Outlined
                width = 64f
                height = 56f
                onClick = { loginWithFacebook() }
            }
        }

        Spacer { height = 24f }

        // Sign up link
        Row {
            horizontalArrangement = Arrangement.Center

            Text("Don't have an account? ") {
                font = Font.BodyMedium
                color = theme.onSurfaceVariant
            }

            Button("Sign Up") {
                variant = ButtonVariant.Text
                onClick = { navigateToSignUp() }
            }
        }
    }
}

Card {
    variant = CardVariant.Elevated
    elevation = ${elevation}f
    padding = ${padding}f
    fillMaxWidth()

    Column {
        spacing = 12f

        Text("${title}") {
            font = Font.TitleMedium
            color = theme.onSurface
        }

        Text("${description}") {
            font = Font.BodyMedium
            color = theme.onSurfaceVariant
        }
    }
}

ScrollView {
    fillMaxSize()

    Column {
        spacing = 8f
        padding = 16f

        forEach(${dataSource}) { item ->
            ListItem {
                title = item.${titleProperty}
                subtitle = item.${subtitleProperty}
                leadingIcon = "${icon}"
                onClick = { handleItemClick(item) }
            }
        }
    }
}

Dialog {
    visible = ${showDialog}
    onDismiss = { ${showDialog} = false }

    Card {
        padding = 24f
        width = 300f

        Column {
            spacing = 16f

            Text("${title}") {
                font = Font.HeadlineSmall
                color = theme.onSurface
            }

            Text("${message}") {
                font = Font.BodyMedium
                color = theme.onSurfaceVariant
            }

            Row {
                fillMaxWidth()
                horizontalArrangement = Arrangement.End
                spacing = 8f

                Button("${cancelLabel}") {
                    variant = ButtonVariant.Text
                    onClick = { ${showDialog} = false }
                }

                Button("${confirmLabel}") {
                    variant = ButtonVariant.Filled
                    onClick = {
                        ${confirmAction}
                        ${showDialog} = false
                    }
                }
            }
        }
    }
}

AppBar {
    title = "${title}"
    navigationIcon = "${navIcon}"
    onNavigationClick = { ${navAction} }

    actions {
        IconButton {
            icon = "search"
            onClick = { openSearch() }
        }

        IconButton {
            icon = "more_vert"
            onClick = { showMenu() }
        }
    }
}

fun ${formName}() = AvaUI {
    theme = Themes.${themeName}

    val ${field1State} = remember { mutableStateOf("") }
    val ${field2State} = remember { mutableStateOf("") }
    val ${field3State} = remember { mutableStateOf("") }

    Column {
        fillMaxSize()
        padding = 24f
        spacing = 16f

        Text("${formTitle}") {
            font = Font.HeadlineMedium
            color = theme.onSurface
        }

        TextField {
            label = "${field1Label}"
            value = ${field1State}.value
            onValueChange = { ${field1State}.value = it }
            fillMaxWidth()
        }

        TextField {
            label = "${field2Label}"
            value = ${field2State}.value
            onValueChange = { ${field2State}.value = it }
            fillMaxWidth()
        }

        TextField {
            label = "${field3Label}"
            value = ${field3State}.value
            onValueChange = { ${field3State}.value = it }
            fillMaxWidth()
        }

        Spacer { height = 24f }

        Button("${submitLabel}") {
            variant = ButtonVariant.Filled
            fillMaxWidth()
            height = 56f
            onClick = { ${submitAction} }
        }
    }
}
